#!/bin/bash

# Auto-Build Environment Setup
# Generated by Planner Agent for Cost Prediction Dashboard

set -e

echo "========================================"
echo "Starting Development Environment"
echo "Cost Prediction Dashboard (Spec 018)"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# INFRASTRUCTURE SERVICES
# ============================================

echo ""
echo "Starting infrastructure services..."

# PostgreSQL (via Docker if not running)
if ! pg_isready -h localhost -p 5432 > /dev/null 2>&1; then
    echo "Starting PostgreSQL..."
    docker-compose up -d postgres &
    wait_for_service 5432 "PostgreSQL"
else
    echo -e "${GREEN}PostgreSQL already running${NC}"
fi

# Redis (via Docker if not running)
if ! redis-cli -h localhost -p 6379 ping > /dev/null 2>&1; then
    echo "Starting Redis..."
    docker-compose up -d redis &
    wait_for_service 6379 "Redis"
else
    echo -e "${GREEN}Redis already running${NC}"
fi

# ============================================
# BACKEND SERVICE
# ============================================

echo ""
echo "Starting backend service..."

# Check Python dependencies
if [ ! -d "venv" ]; then
    echo "Creating Python virtual environment..."
    python3 -m venv venv
fi

source venv/bin/activate
pip install -r requirements.txt > /dev/null 2>&1

# Run database migrations
echo "Running database migrations..."
alembic upgrade head || echo -e "${YELLOW}No migrations to run${NC}"

# Start backend
echo "Starting FastAPI backend..."
cd src
uvicorn main:app --host 0.0.0.0 --port 8000 --reload &
BACKEND_PID=$!
cd ..

wait_for_service 8000 "FastAPI Backend"

# ============================================
# FRONTEND SERVICE
# ============================================

echo ""
echo "Starting frontend service..."

cd web

# Check Node dependencies
if [ ! -d "node_modules" ]; then
    echo "Installing npm dependencies..."
    npm install > /dev/null 2>&1
fi

# Start Vite dev server
echo "Starting Vite dev server..."
npm run dev &
FRONTEND_PID=$!
cd ..

# Note: Vite proxy forwards to backend on 8000, so frontend runs on different port
# Check Vite's actual port (usually 5173)
sleep 3

# ============================================
# ENVIRONMENT VERIFICATION
# ============================================

echo ""
echo "Verifying environment variables..."

# Check required env vars for this feature
if [ -z "$SMTP_HOST" ]; then
    echo -e "${YELLOW}Warning: SMTP_HOST not set - budget alerts will not work${NC}"
fi

if [ -z "$GOOGLE_CALENDAR_CREDENTIALS_JSON" ]; then
    echo -e "${YELLOW}Warning: GOOGLE_CALENDAR_CREDENTIALS_JSON not set - calendar integration will not work${NC}"
fi

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Backend API:  http://localhost:8000"
echo "  API Docs:     http://localhost:8000/docs"
echo "  Frontend:     http://localhost:5173 (proxies to backend)"
echo "  PostgreSQL:   localhost:5432"
echo "  Redis:        localhost:6379"
echo ""
echo "Cost Forecast Endpoints (after implementation):"
echo "  GET  /api/v1/metrics/spot/cost-forecast/{gpu_name}"
echo "  POST /api/v1/metrics/spot/optimal-timing"
echo "  GET  /api/v1/metrics/spot/forecast-accuracy/{gpu_name}"
echo "  POST /api/v1/metrics/spot/budget-alerts"
echo "  GET  /api/v1/metrics/spot/calendar-events"
echo ""
echo "Process IDs:"
echo "  Backend:  $BACKEND_PID"
echo "  Frontend: $FRONTEND_PID"
echo ""
echo "To stop services:"
echo "  kill $BACKEND_PID $FRONTEND_PID"
echo "  docker-compose down"
echo ""
echo -e "${GREEN}Ready for implementation!${NC}"
