     1→import { useState, useCallback } from "react";
     2→
     3→export const useModal = (initialState: boolean = false) => {
     4→  const [isOpen, setIsOpen] = useState(initialState);
     5→
     6→  const openModal = useCallback(() => setIsOpen(true), []);
     7→  const closeModal = useCallback(() => setIsOpen(false), []);
     8→  const toggleModal = useCallback(() => setIsOpen((prev) => !prev), []);
     9→
    10→  return { isOpen, openModal, closeModal, toggleModal };
    11→};
    12→

