     7→export const FOCUSABLE_SELECTOR = [
     8→  "button:not([disabled])",
     9→  "input:not([disabled])",
    10→  "select:not([disabled])",
    11→  "textarea:not([disabled])",
    12→  "a[href]",
    13→  "[tabindex]:not([tabindex='-1'])",
    14→].join(", ");
    15→
    16→/**
    17→ * Gets all focusable elements within a container element.
    18→ * @param container - The container element to search within
    19→ * @returns An array of focusable HTML elements
    20→ */
    21→const getFocusableElements = (container: HTMLElement): HTMLElement[] => {
    22→  const elements = container.querySelectorAll<HTMLElement>(FOCUSABLE_SELECTOR);
    23→  return Array.from(elements).filter(
    24→    (el) => el.offsetParent !== null && !el.hasAttribute("disabled")
    25→  );
    26→};
