    28→/**
    29→ * A React hook that implements focus trapping for modal components.
    30→ *
    31→ * Features:
    32→ * - Stores the previously focused element when activated
    33→ * - Traps Tab/Shift+Tab within the container
    34→ * - Wraps focus from last to first element and vice versa
    35→ * - Returns focus to the trigger element on cleanup
    36→ *
    37→ * @param containerRef - A ref to the container element to trap focus within
    38→ * @param isActive - Whether the focus trap is currently active
    39→ *
    40→ * @example
    41→ * ```tsx
    42→ * const dialogRef = useRef<HTMLDivElement>(null);
    43→ * useFocusTrap(dialogRef, isOpen);
    44→ *
    45→ * return <div ref={dialogRef}>{children}</div>;
    46→ * ```
    47→ */
    48→export const useFocusTrap = (
    49→  containerRef: RefObject<HTMLElement | null>,
    50→  isActive: boolean
    51→): void => {
    52→  // Store the element that was focused before the trap was activated
    53→  const previouslyFocusedRef = useRef<HTMLElement | null>(null);
    54→
    55→  useEffect(() => {
    56→    if (!isActive) return;
    57→
    58→    const container = containerRef.current;
    59→    if (!container) return;
    60→
    61→    // Store the currently focused element before moving focus
    62→    previouslyFocusedRef.current = document.activeElement as HTMLElement;
    63→
    64→    // Focus the first focusable element in the container
    65→    const focusableElements = getFocusableElements(container);
    66→    if (focusableElements.length > 0) {
    67→      // Small delay to ensure the container is rendered
    68→      requestAnimationFrame(() => {
    69→        focusableElements[0]?.focus();
    70→      });
    71→    } else {
    72→      // If no focusable elements, focus the container itself
    73→      container.setAttribute("tabindex", "-1");
    74→      container.focus();
    75→    }
    76→
    77→    /**
    78→     * Handle keyboard events to trap focus within the container.
    79→     * - Tab: Move to next focusable element, wrap to first if at end
    80→     * - Shift+Tab: Move to previous focusable element, wrap to last if at start
    81→     */
    82→    const handleKeyDown = (event: KeyboardEvent) => {
    83→      if (event.key !== "Tab") return;
    84→
    85→      const focusable = getFocusableElements(container);
    86→      if (focusable.length === 0) return;
    87→
    88→      const firstElement = focusable[0];
    89→      const lastElement = focusable[focusable.length - 1];
    90→      const activeElement = document.activeElement;
    91→
    92→      if (event.shiftKey) {
    93→        // Shift+Tab: moving backwards
    94→        if (activeElement === firstElement || !container.contains(activeElement)) {
    95→          event.preventDefault();
    96→          lastElement.focus();
    97→        }
    98→      } else {
    99→        // Tab: moving forwards
   100→        if (activeElement === lastElement || !container.contains(activeElement)) {
   101→          event.preventDefault();
   102→          firstElement.focus();
   103→        }
   104→      }
   105→    };
   106→
   107→    // Add the keyboard event listener
   108→    document.addEventListener("keydown", handleKeyDown);
   109→
   110→    // Cleanup function: remove listener and return focus
   111→    return () => {
   112→      document.removeEventListener("keydown", handleKeyDown);
   113→
   114→      // Return focus to the previously focused element
   115→      if (
   116→        previouslyFocusedRef.current &&
   117→        typeof previouslyFocusedRef.current.focus === "function"
   118→      ) {
   119→        previouslyFocusedRef.current.focus();
   120→      }
   121→    };
   122→  }, [isActive, containerRef]);
   123→};
   124→
   125→export default useFocusTrap;
