     1→import { createContext, useContext, useState, useCallback, useMemo } from 'react'
     2→import { X, CheckCircle, AlertCircle, AlertTriangle, Info } from 'lucide-react'
     3→
     4→const ToastContext = createContext(null)
     5→
     6→// Variants - dark background with colored accent
     7→const variants = {
     8→  success: {
     9→    icon: CheckCircle,
    10→    accent: 'bg-green-500',
    11→    iconColor: 'text-green-400',
    12→    textColor: 'text-white'
    13→  },
    14→  error: {
    15→    icon: AlertCircle,
    16→    accent: 'bg-red-500',
    17→    iconColor: 'text-red-400',
    18→    textColor: 'text-white'
    19→  },
    20→  warning: {
    21→    icon: AlertTriangle,
    22→    accent: 'bg-yellow-500',
    23→    iconColor: 'text-yellow-400',
    24→    textColor: 'text-white'
    25→  },
    26→  info: {
    27→    icon: Info,
    28→    accent: 'bg-blue-500',
    29→    iconColor: 'text-blue-400',
    30→    textColor: 'text-white'
    31→  }
    32→}
    33→
    34→function ToastItem({ id, message, type = 'info', onClose }) {
    35→  const variant = variants[type] || variants.info
    36→  const Icon = variant.icon
    37→
    38→  return (
    39→    <div
    40→      className="toast flex items-stretch bg-gray-900 rounded-lg shadow-2xl shadow-black/50 overflow-hidden animate-toast-in border border-gray-700"
    41→      style={{ minWidth: '320px', maxWidth: '500px' }}
    42→      role="alert"
    43→      aria-live="polite"
    44→    >
    45→      {/* Color accent bar on left */}
    46→      <div className={`w-1.5 ${variant.accent} flex-shrink-0`} />
    47→
    48→      {/* Content */}
    49→      <div className="flex items-center gap-3 px-4 py-3.5 flex-1">
    50→        <Icon className={`w-5 h-5 ${variant.iconColor} flex-shrink-0`} />
    51→        <p className={`flex-1 text-sm font-medium ${variant.textColor}`}>{message}</p>
    52→        <button
    53→          onClick={() => onClose(id)}
    54→          className="text-gray-500 hover:text-white transition-colors p-1 rounded hover:bg-gray-800"
    55→          aria-label="Fechar notificacao"
    56→        >
    57→          <X className="w-4 h-4" />
    58→        </button>
    59→      </div>
    60→    </div>
    61→  )
    62→}
    63→
    64→export function ToastProvider({ children }) {
    65→  const [toasts, setToasts] = useState([])
    66→
    67→  const removeToast = useCallback((id) => {
    68→    setToasts(prev => prev.filter(toast => toast.id !== id))
    69→  }, [])
    70→
    71→  const addToast = useCallback((message, type = 'info', duration = 4000) => {
    72→    const id = Date.now() + Math.random()
    73→    setToasts(prev => [...prev, { id, message, type }])
    74→
    75→    if (duration > 0) {
    76→      setTimeout(() => {
    77→        removeToast(id)
    78→      }, duration)
    79→    }
    80→
    81→    return id
    82→  }, [removeToast])
    83→
    84→  const toast = useMemo(() => ({
    85→    success: (message, duration) => addToast(message, 'success', duration),
    86→    error: (message, duration) => addToast(message, 'error', duration),
    87→    warning: (message, duration) => addToast(message, 'warning', duration),
    88→    info: (message, duration) => addToast(message, 'info', duration),
    89→    remove: removeToast
    90→  }), [addToast, removeToast])
    91→
    92→  return (
    93→    <ToastContext.Provider value={toast}>
    94→      {children}
    95→      {/* Toast Container - TOP CENTER */}
    96→      <div className="toast-container fixed top-4 left-1/2 -translate-x-1/2 z-[99999] flex flex-col items-center gap-3 pointer-events-none">
    97→        {toasts.map(t => (
    98→          <div key={t.id} className="pointer-events-auto">
    99→            <ToastItem
   100→              id={t.id}
   101→              message={t.message}
   102→              type={t.type}
   103→              onClose={removeToast}
   104→            />
   105→          </div>
   106→        ))}
   107→      </div>
   108→      <style>{`
   109→        @keyframes toast-in {
   110→          0% {
   111→            opacity: 0;
   112→            transform: translateY(-100%) scale(0.95);
   113→          }
   114→          100% {
   115→            opacity: 1;
   116→            transform: translateY(0) scale(1);
   117→          }
   118→        }
   119→        .animate-toast-in {
   120→          animation: toast-in 0.3s ease-out;
   121→        }
   122→      `}</style>
   123→    </ToastContext.Provider>
   124→  )
   125→}
   126→
   127→export function useToast() {
   128→  const context = useContext(ToastContext)
   129→  if (!context) {
   130→    throw new Error('useToast must be used within a ToastProvider')
   131→  }
   132→  return context
   133→}
   134→
   135→export default ToastProvider
   136→

